INSERT INTO `xtt_article` VALUES ('1', '测试0000', '11111111111', '<img title=\"点击查看原图\" border=\"0\" alt=\"点击查看原图\" src=\"http://www.xietaotao.cn/content/uploadfile/201211/4f5ff581ac03122baf7eb057b7e0a0bd20121115095059.jpg\" width=\"315\" height=\"337\" />', 'http://www.me.me/Data/upload/image/article/1/yt_3165799a056f7b453ed86904513ca977.jpg', '2015-03-22 13:15:33', '1', '1', '100', '100', '1', '1', '0', '');
